package com.cheersondemand.frameworks.retrofit;import com.cheersondemand.model.AuthenticationResponse;import com.cheersondemand.model.CategoriesResponse;import com.cheersondemand.model.CategoryRequest;import com.cheersondemand.model.GuestUserCreateResponse;import com.cheersondemand.model.LoginRequest;import com.cheersondemand.model.LogoutRequest;import com.cheersondemand.model.SignUpRequest;import com.cheersondemand.model.SocialLoginRequest;import retrofit2.Call;import retrofit2.http.Body;import retrofit2.http.GET;import retrofit2.http.Header;import retrofit2.http.POST;import retrofit2.http.Query;public interface WebServices {    @POST("/cheers_on_demand/api/v1/users")    Call<AuthenticationResponse> signUpUsingEmail(@Body SignUpRequest signUpRequest);    @POST("/cheers_on_demand/api/v1/oauth/token")    Call<AuthenticationResponse> loginUsingEmail(@Body LoginRequest loginRequest);    @POST("/cheers_on_demand/api/v1/oauth/token")    Call<AuthenticationResponse> loginUsingSocial(@Body SocialLoginRequest socialLoginRequest);    @POST("/cheers_on_demand/api/v1/categories")    Call<CategoriesResponse> getBrands(@Body CategoryRequest uuid);    @GET("/cheers_on_demand/api/v1/categories")    Call<CategoriesResponse> getGuestBrands(@Query("uuid") String uuid);    @POST("/cheers_on_demand/api/v1/users/create_guest_user")    Call<GuestUserCreateResponse> createGuestUser(@Body CategoryRequest uuid);    @POST("/cheers_on_demand/api/v1/categories")    Call<CategoriesResponse> getBrands(@Header("Authorization") String token,@Body CategoryRequest uuid);    @POST("/cheers_on_demand/api/v1/oauth/revoke")    Call<String> Logout(@Body LogoutRequest logoutRequest);}